$(document).ready(function(){$("#summernote-basic").summernote({placeholder:"Write something...",height:230,callbacks:{onInit:function(o){$(o.editor).find(".custom-control-description").addClass("custom-control-label").parent().removeAttr("for")}}}),$("#summernote-airmode").summernote({airMode:!0,callbacks:{onInit:function(o){$(o.editor).find(".custom-control-description").addClass("custom-control-label").parent().removeAttr("for")}}})});